import { Form, Button } from 'react-bootstrap'
import axios from 'axios'
import { useState } from 'react'

const AddProduct = () => {
    const [productName, setProductName] = useState('')
    const [productPrice, setProductPrice] = useState(0)
    const [productImg, setProductImg] = useState('')
    const [productAmount, setProductAmount] = useState(0)
    const [productDesc, setProductDesc] = useState(0)
    const [productType, setProductType] = useState(0)
    const [productOption, setProductOption] = useState('')
    const [productOptionArray, setProductOptionArray] = useState([])

    const login = async () => {
        MakeProductOptionArray()

        var data = {
            productName: productName,
            productPrice: productPrice,
            productImg: productImg,
            productAmount: productAmount,
            productDesc: productDesc,
            productType: productType,
            productOptionArray: productOptionArray,
        }

        console.log(data)
    }

    const StoreCookie = async () => {
        await axios.get("auth/getcookie")
            .then(res => {
                localStorage.setItem("user_id", res.data)
                console.log(res.data)
            })
            .catch(err => console.log("heeza"))
    }

    const MakeProductOptionArray = () => {
        const x = 'asd, qwerty'
        var temp = ''

        x.forEach(x => {
            if (x != ',' || x != ' ') {
                temp += x
            }
            else if (x == ',') {
                setProductOptionArray([...productOptionArray, temp])
                temp = ''
            }
        });


    }

    return (
        <Form style={{ marginTop: '10%', paddingLeft: '15%' }}>
            <Form.Group className="mb-3" >
                <Form.Label>Product Name</Form.Label>
                <Form.Control type="text" placeholder="ex. iPhone X"
                    style={{ width: '75%' }}
                    onChange={e => setProductName(e.target.value)} />

            </Form.Group>

            <Form.Group className="mb-3" >
                <Form.Label>Price</Form.Label>
                <Form.Control type="number" placeholder="ex. 750"
                    style={{ width: '75%' }}
                    onChange={e => setProductPrice(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" >
                <Form.Label>Image Source</Form.Label>
                <Form.Control type="text" placeholder="ex. https://media.comicbook.com/2020/12/naruto-1249229.jpeg"
                    style={{ width: '75%' }}
                    onChange={e => setProductImg(e.target.value)} />
            </Form.Group>
            <Form.Group className="mb-3" >
                <Form.Label>Product Amount</Form.Label>
                <Form.Control type="number" placeholder="ex. 23"
                    style={{ width: '75%' }}
                    onChange={e => setProductAmount(e.target.value)} />
            </Form.Group>
            <Form.Group className="mb-3" >
                <Form.Label>Product Type</Form.Label>
                <Form.Control
                    as="select"
                    style={{ width: '75%' }}
                    onChange={e => setProductType(e.target.value)}
                >
                    <option value="weapon">Weapon</option>
                    <option value="clothes">Clothes</option>
                    <option value="phone">Phone</option>
                    <option value="pet">Pet</option>

                </Form.Control>
            </Form.Group>
            <Form.Group className="mb-3" >
                <Form.Label>Product Options</Form.Label>
                <Form.Control type="text" placeholder="ex. 23"
                    style={{ width: '75%' }}
                    onChange={e => setProductOption(e.target.value)} />
            </Form.Group>
            <Form.Group className="mb-3" >
                <Form.Label>Product Description</Form.Label>
                <Form.Control type="text" placeholder="ex. 23"
                    style={{ width: '75%' }}
                    onChange={e => setProductDesc(e.target.value)} />
            </Form.Group>


            <Button variant="primary" onClick={() => login()}>
                Submit
            </Button>
        </Form>
    )
}

export default AddProduct
