import { Card, Button, Row, Col, Image } from 'react-bootstrap'
import '../App.css'
import axios from 'axios'
import { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'


const UserProfile = () => {
    const [user, setUser] = useState({})
    const [chooseType, setChooseType] = useState('All')
    const [product, setProduct] = useState([])
    const [transaction, setTransaction] = useState([])

    const GetUser = async () => {
        await axios.get("user/getbyid/" + localStorage.getItem("user_id"))
            .then(res => setUser(res.data))
    }

    const GetAllProduct = async () => {
        await axios.get("product/getall")
            .then(res => setProduct(res.data))
    }

    const GetTransaction = async () => {
        await axios.get("/transaction/getbyuserid/" + localStorage.getItem("user_id"))
            .then(res => {
                console.log(res.data[0]._id)
                setTransaction(res.data)
            }
            )
    }

    const MergeProductToTransaction = () => {
        var arrayList = []


    }

    useEffect(() => {

        GetUser()
        GetTransaction()
        // GetAllProduct()
        // console.log(product)

    }, [])


    const MapProductType = () => {
        var TypeList = ['All', 'Unpaid', 'Paid', 'Onshipping', 'Complete']
        return (
            TypeList.map((item, i) =>
            (
                <Button

                    style={buttonTypeStyle}
                    onClick={() => {
                        setChooseType(item)

                    }}
                    variant={chooseType === item ? "info" : 'outline-secondary'}
                    key={i}

                >{item}</Button>
            ))
        )
    }

    const ProductList = () => {
        return (
            <div >

                <Card style={{}} className="" >
                    <Row>
                        <Col md={3} >
                            <div style={{ padding: 10 }}>
                                <Image src={"asd"}
                                    style={imageStyle}
                                    className="imageObjectFit"
                                    rounded
                                />

                            </div>
                        </Col>
                        <Col>
                            <Row className="mt-4 mb-2">
                                <Col md={4}>
                                    <span style={spanStyleLeft}>Product Name</span>
                                </Col>
                                <Col>
                                    <span style={spanStyleRight} className='text-secondary'>{transaction[0]._id}</span>

                                </Col>

                            </Row>
                            <Row>
                                <Col md={4}>
                                    <span style={spanStyleLeft}>Product Amount</span>
                                </Col>

                                <Col>
                                    <span style={spanStyleRight} className='text-secondary'>{'asd'}</span>


                                </Col>

                            </Row>

                            <Row className="mt-2">
                                <Col style={{ marginLeft: 30 }} md={1}>
                                    <Button variant="outline-danger" >Delete</Button>

                                </Col>


                            </Row>
                        </Col>
                    </Row>
                </Card >




            </div >
        )
    }


    return (
        <>
            <Card
                className="justify-content-md-center"
                style={cardStyle}
                border="light">
                <Card.Body>
                    <Row>
                        <Col md={4} >
                            <Card style={cardStyle_inside_left} >
                                <Card.Body className="text-center">
                                    <Row >
                                        <Col >
                                            <Card.Img
                                                src="holder.js/100px180"
                                                className="imageStyle_profile rounded-circle"
                                                style={imageStyle}
                                                src="https://cdn.staticneo.com/w/naruto/Maito_gai_smile.jpg" />
                                        </Col>
                                    </Row>
                                    <Row>
                                        <Col >
                                            <Card.Title style={userTitleStyle}>{user.firstname + " " + user.lastname}</Card.Title>

                                            <Button variant="outline-primary" style={{ marginTop: 10 }}>Edit Profile</Button>
                                            <div />
                                            <Link to="/myproduct">
                                                <Button variant="outline-primary" style={{ marginTop: 10 }}>My Product</Button>
                                            </Link>
                                        </Col>
                                    </Row>
                                </Card.Body>
                            </Card>
                        </Col>

                        <Col md={8}>
                            <Card style={cardStyle_inside_right}>

                                <Card.Body>
                                    <Row>
                                        <Col style={{ textAlign: 'center' }}>
                                            {MapProductType()}
                                        </Col>

                                    </Row>
                                    <Col>
                                        <hr style={hrStyle} />
                                    </Col>
                                    <Row>
                                        {ProductList()}

                                    </Row>
                                    <Col>
                                        <hr style={hrStyle} />
                                    </Col>
                                    <Row>
                                        <Col md={4}>
                                            <span style={spanStyleLeft}>detail #2</span>
                                        </Col>
                                        <Col>
                                            <span style={spanStyleRight} className='text-secondary'>list #2</span>

                                        </Col>

                                    </Row>
                                    <Col>
                                        <hr style={hrStyle} />
                                    </Col>

                                </Card.Body>

                            </Card></Col>
                    </Row>
                </Card.Body>
            </Card >
        </>
    )
}

const buttonTypeStyle = {
    marginRight: 20,
    width: 110

}

const cardStyle = {
    width: '100%',
    height: 600,
    marginTop: 40,
    backgroundColor: '#FAFAFA'
}

const imageStyle = {
    height: 240,
    width: 240
}

const cardStyle_inside_left = {
    paddingTop: 20,
    marginTop: 20,
    marginLeft: 15,
    width: 330,
    height: 450
}
const cardStyle_inside_right = {
    marginTop: 20,
    width: 740,
    height: 450
}

const userTitleStyle = {
    fontSize: 25,
    marginTop: 15
}

const userHeader = {
    backgroundColor: 'white',
    fontSize: 22,
    fontWeight: 600,
    paddingLeft: 43,
    paddingTop: 12,
    paddingBottom: 12
}

const hrStyle = {
    marginTop: 20,
    marginBottom: 20
}

const spanStyleLeft = {
    paddingLeft: 30,
    fontWeight: 500
}

const spanStyleRight = {
    paddingLeft: 20,
    fontWeight: 500
}
export default UserProfile;
