import { Card, Row, Col, Button } from 'react-bootstrap'
import '../App.css'
import Image from 'react-bootstrap/Image'
import { AiFillStar, AiOutlineHeart } from 'react-icons/ai';
import axios from 'axios';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
const Cart = () => {
    const [product, setProduct] = useState({})
    let { product_id_params } = useParams()
    const [itemAmount, setItemAmount] = useState(0)

    useEffect(() => {
        GetProduct()
        console.log(product_id_params)
    }, [])

    const GetProduct = async () => {
        axios.get("/product/getbyid/" + "61d5812b1bd9d464dcf721fc")
            .then(res => {
                setProduct(res.data)
                setItemAmount(res.data.product_amount)
            })

    }


    return (
        <div >

            <Card style={{}} className="mt-5">
                <Row>
                    <Col md={3} >
                        <div style={{ padding: 10 }}>
                            <Image src={product.product_image}
                                style={imageStyle}
                                className="imageObjectFit"
                                rounded
                            />

                        </div>
                    </Col>
                    <Col>
                        <Row className="mt-4">
                            <Col md={3}>
                                <span style={spanStyleLeft}>Product Name</span>
                            </Col>
                            <Col>
                                <span style={spanStyleRight} className='text-secondary'>{product.product_name}</span>

                            </Col>

                        </Row>
                        <Col>
                            <hr style={hrStyle} />
                        </Col>
                        <Row>
                            <Col md={3}>
                                <span style={spanStyleLeft}>Product Amount</span>
                            </Col>

                            <Col md={3}>
                                <Button
                                    style={{ fontSize: 15 }}
                                    variant={'outline-secondary'}
                                    onClick={() => {
                                        if (itemAmount > 0)
                                            setItemAmount(itemAmount - 1)
                                    }
                                    }
                                >-</Button>

                                <Button
                                    style={{ fontSize: 15 }}
                                    variant={'outline-secondary'}
                                    disabled>{itemAmount}</Button>

                                <Button
                                    variant={'outline-secondary'}
                                    style={{ fontSize: 15 }}
                                    onClick={() => {
                                        if (itemAmount < product.product_amount) {
                                            setItemAmount(itemAmount + 1)
                                        }
                                    }}
                                >+</Button>
                            </Col>

                        </Row>
                        <Col>
                            <hr style={hrStyle} />
                        </Col>
                        <Row>
                            <Col md={3}>
                                <span style={spanStyleLeft}>Product Amount</span>
                            </Col>

                        </Row>
                    </Col>
                </Row>
            </Card>
        </div >
    )
}

const imageStyle = {
    width: 200,
    height: 200,
    marginBottom: 20


}



const hrStyle = {
    marginTop: 15,
    marginBottom: 10
}

const spanStyleLeft = {
    paddingLeft: 30,
    fontWeight: 500
}

const spanStyleRight = {
    paddingLeft: 20,
    fontWeight: 500
}

export default Cart
