import { Card, Row, Col, Button, FormCheck, Form } from 'react-bootstrap'
import '../App.css'
import Image from 'react-bootstrap/Image'
import { AiFillStar, AiOutlineHeart } from 'react-icons/ai';
import axios from 'axios';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { BsCartCheck } from "react-icons/bs"
const Cart = ({ item }) => {
    const [cart, setCart] = useState([])
    let { product_id_params } = useParams()
    const [asd, setAsd] = useState([])
    const [productList, setProductList] = useState([])
    const [selectedCart, setSelectedCart] = useState('')

    return (
        <div >

            <Card style={{}} className="" >
                <Row>
                    <Col md={2} >
                        <div style={{ padding: 10 }}>
                            <Image
                                style={imageStyle}
                                className="imageObjectFit"
                                rounded
                            />

                        </div>
                    </Col>
                    <Col>
                        <Row className="mt-4 mb-2">
                            <Col md={3}>
                                <span style={spanStyleLeft}>Product Name</span>
                            </Col>
                            <Col>
                                <span style={spanStyleRight} className='text-secondary'>{item.product_name}</span>

                            </Col>

                        </Row>
                        <Row>
                            <Col md={3}>
                                <span style={spanStyleLeft}>Product Amount</span>
                            </Col>

                            <Col>
                                <span style={spanStyleRight} className='text-secondary'>{item.cart_product_amount}</span>


                            </Col>

                        </Row>

                        <Row className="mt-2">
                            <Col style={{ marginLeft: 30 }} md={1}>
                                <Button variant="outline-danger" >Delete</Button>

                            </Col>


                        </Row>
                    </Col>
                </Row>
            </Card >




        </div >
    )
}

const imageStyle = {
    width: 150,
    height: 150,
    color: 'red'



}

const checkboxStyle = {
    width: 35,
    height: 35
}

const hrStyle = {
    marginTop: 20,
    marginBottom: 20
}

const spanStyleLeft = {
    paddingLeft: 30,
    fontWeight: 500
}

const spanStyleRight = {
    paddingLeft: 20,
    fontWeight: 500
}

export default Cart
