import { Form, Button } from 'react-bootstrap'
import axios from 'axios'
import { useState } from 'react'

const Login = () => {
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [firstname, setFirstname] = useState('')
    const [lastname, setLastname] = useState('')
    const [gender, setGender] = useState('')
    const [email, setEmail] = useState('')
    const [province, setProvince] = useState('')
    const [address, setAddress] = useState('')
    const [postcode, setPostcode] = useState('')
    const [storename, setStorename] = useState('')

    const login = async () => {
        var data = {
            username: username,
            password: password
        }
        await axios.post("http://localhost:8080/auth/login", data)
            .then(res => StoreCookie())

        //.then(res => console.log("za"))
    }

    const StoreCookie = async () => {
        await axios.get("auth/getcookie")
            .then(res => {
                localStorage.setItem("user_id", res.data)
                console.log("object")
                window.location.href = "/"
            })

    }


    return (
        <Form style={{ marginTop: '20%', paddingLeft: '30%' }}>
            <Form.Group className="mb-3" >
                <Form.Label>Username</Form.Label>
                <Form.Control type="text" placeholder="Enter Username"
                    style={{ width: '50%' }}
                    onChange={e => setUsername(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password"
                    style={{ width: '50%' }}
                    onChange={e => setPassword(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" >
                <Form.Label>Firstname</Form.Label>
                <Form.Control type="text" placeholder="Enter Firstname"
                    style={{ width: '50%' }}
                    onChange={e => setUsername(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" >
                <Form.Label>Lastname</Form.Label>
                <Form.Control type="text" placeholder="Enter Lastname"
                    style={{ width: '50%' }}
                    onChange={e => setUsername(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" >
                <Form.Label>Gender</Form.Label>
                <Form.Control
                    as="select"
                    style={{ width: '30%', fontWeight: 'bold' }}
                    onChange={e => setType(e.target.value)}
                >
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                </Form.Control>
            </Form.Group>

            <Form.Group className="mb-3" >
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" placeholder="Enter Email"
                    style={{ width: '50%' }}
                    onChange={e => setUsername(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" >
                <Form.Label>Storename</Form.Label>
                <Form.Control type="text" placeholder="Enter Storename"
                    style={{ width: '50%' }}
                    onChange={e => setUsername(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" >
                <Form.Label>Province</Form.Label>
                <Form.Control type="text" placeholder="Enter Province"
                    style={{ width: '50%' }}
                    onChange={e => setUsername(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" >
                <Form.Label>Address</Form.Label>
                <Form.Control type="text" placeholder="Enter Address"
                    style={{ width: '50%' }}
                    onChange={e => setUsername(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" >
                <Form.Label>Postcode</Form.Label>
                <Form.Control type="text" placeholder="Enter Postcode"
                    style={{ width: '50%' }}
                    onChange={e => setUsername(e.target.value)} />
            </Form.Group>


            <Button variant="primary" onClick={() => login()}>
                Submit
            </Button>
        </Form>
    )
}

export default Login
