import { Card, Row, Col, Button } from 'react-bootstrap'
import '../App.css'
import Image from 'react-bootstrap/Image'
import { AiFillStar, AiOutlineHeart } from 'react-icons/ai';
import axios from 'axios';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
const Cart = () => {
    const [cart, setCart] = useState([])
    let { product_id_params } = useParams()
    const [asd, setAsd] = useState([])
    const [productList, setProductList] = useState([])


    useEffect(() => {
        GetCartInfo()
    }, [])

    const GetCartInfo = async () => {
        await axios.get("/cart/getbyuserid/" + localStorage.getItem('user_id'))
            .then(res => {
                setCart(res.data)
            })

    }


    const SingleCart = () => {
        return (

            cart.map((item, i) => (
                <Card style={{}} className="mt-5" key={i}>
                    <Row>
                        <Col md={3} >
                            <div style={{ padding: 10 }}>
                                <Image src={item.product_image}
                                    style={imageStyle}
                                    className="imageObjectFit"
                                    rounded
                                />

                            </div>
                        </Col>
                        <Col>
                            <Row className="mt-4">
                                <Col md={3}>
                                    <span style={spanStyleLeft}>Product Name</span>
                                </Col>
                                <Col>
                                    <span style={spanStyleRight} className='text-secondary'>{item.product_name}</span>

                                </Col>

                            </Row>
                            <Col>
                                <hr style={hrStyle} />
                            </Col>
                            <Row>
                                <Col md={3}>
                                    <span style={spanStyleLeft}>Product Amount</span>
                                </Col>

                                <Col md={3}>
                                    <Button
                                        style={{ fontSize: 15 }}
                                        variant={'outline-secondary'}
                                        onClick={() => {
                                            if (item.cart_product_amount > 1)
                                                item.cart_product_amount = item.cart_product_amount - 1
                                        }}

                                    >-</Button>

                                    <Button
                                        style={{ fontSize: 15 }}
                                        variant={'outline-secondary'}
                                        disabled>{item.cart_product_amount}</Button>

                                    <Button
                                        variant={'outline-secondary'}
                                        style={{ fontSize: 15 }}
                                        onClick={() => {
                                            // var asd = cart
                                            // asd[0].cart_product_amount = 55
                                            //setCart([cart, asd])
                                            item.cart_product_amount = item.cart_product_amount + 1
                                            // console.log(cart)
                                        }}
                                    >+</Button>
                                </Col>

                            </Row>
                            <Col>
                                <hr style={hrStyle} />
                            </Col>
                            <Row>
                                <Col style={{ marginLeft: 30 }}>
                                    <Button variant="outline-danger" onClick={() => console.log(cart)}>Delete</Button>
                                    <Button variant="outline-success" style={{ marginLeft: 10 }}>Save</Button>
                                </Col>

                            </Row>
                        </Col>
                    </Row>
                </Card >

            )))
    }

    return (
        <div >

            {
                SingleCart()
            }

        </div >
    )
}

const imageStyle = {
    width: 200,
    height: 200


}



const hrStyle = {
    marginTop: 20,
    marginBottom: 20
}

const spanStyleLeft = {
    paddingLeft: 30,
    fontWeight: 500
}

const spanStyleRight = {
    paddingLeft: 20,
    fontWeight: 500
}

export default Cart
